var mmPaste = false;
var mmScrollbarPosition = false;

try {
  mmPaste = this.mPrefs.getBoolPref("middlemouse.paste");
}
catch (ex) {
}

try {
  mmScrollbarPosition = this.mPrefs.getBoolPref("middlemouse.scrollbarPosition");
